import React, { Component, lazy, Suspense } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import {
  Badge,
  Button,
  ButtonDropdown,
  ButtonGroup,
  ButtonToolbar,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  CardTitle,
  CardText,
  Col,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Dropdown,
  Progress,
  Row,
  Table,
  FormGroup, Label, Input
} from 'reactstrap';
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips';
import { getStyle, hexToRgba } from '@coreui/coreui/dist/js/coreui-utilities'

class Campaign extends Component {
  constructor(props) {
    super(props);

     this.toggle = this.toggle.bind(this);
    // this.onRadioBtnClick = this.onRadioBtnClick.bind(this);

    this.state = {
      dropdownOpen: false,
      radioSelected: 2,
      items: []
    };
  }

  // toggle() {
  //   this.setState(prevState => ({
  //     dropdownOpen: !prevState.dropdownOpen
  //   }));
  // }
  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen
    });
  }

  // onRadioBtnClick(radioSelected) {
  //   this.setState({
  //     radioSelected: radioSelected,
  //   });
  // }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>
  handleSearch(){

  }


  componentDidMount() {
    fetch('http://127.0.0.1:5000/getDetail')
    .then(resp => resp.json())
    .then((resp) => {
     this.setState({
       items: resp.CList
     });
   })
  }



  render() {
    const {items} = this.state;
    return (
      <div className="animated fadeIn">
        <div>
          <Row>
            <Col sm="6"><h2>Campaigns</h2></Col>
            <Col sm="4"></Col>
            <Col sm="2"> <Button style={{color:"white", backgroundColor: "black"}} size="sm"> &#43;&#160; &#160;Campaign</Button></Col>
          </Row>
        </div> <br/>
          <div> 
            <Row> 
              <Col sm="4"> <Label for="exampleSearch">Your Campaigns</Label></Col>
              <Col sm="1">
                  <Label for="exampleSearch">Search</Label>   
              </Col>
              <Col sm="3">
                  <Input
                    type="search"
                    name="search"
                    id="exampleSearch"
                    placeholder="search placeholder"
                    size="sm"
                    onClick = {this.handleSearch}
                  /> 
              </Col>
              <Col sm="4"> </Col>
            </Row>
          </div>
          <br/>

        <div className ="CampaignPortofolio" style={{display:"Grid", gridTemplateColumns:"190px 190px 190px 190px 190px", gridGap:20}}>
          {items.map(item => (     
            <Card>
              <CardHeader style={{fontSize:20}}>{item.CampaignName} 
              <Dropdown isOpen={this.state.dropdownOpen} toggle={this.toggle}>
                <DropdownToggle >
                <i class="fa fa-ellipsis-v fa-pull-right"></i>
                </DropdownToggle>
                <DropdownMenu>
                  <DropdownItem>Edit</DropdownItem>
                  <DropdownItem>Duplicate</DropdownItem>
                  <DropdownItem>Archive</DropdownItem>
                </DropdownMenu>
              </Dropdown>

              </CardHeader>
              <CardBody>
                <CardText>{item.Channel}</CardText>
              </CardBody>
              <CardFooter className ={(item.Status)==="Completed"? 'completed':(item.Status)==="Running"?'running': (item.Status)==="Scheduled"?'scheduled':(item.Status)==="Archived"?'archived':(item.Status)==="Paused"?'paused':'draft' }> {item.Status} </CardFooter>
            </Card>
          ))}
        </div>

      </div>      
    );
  }
}
export default Campaign;
