import React, { Component, lazy, Suspense } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import {
  Badge,
  Button,
  ButtonDropdown,
  ButtonGroup,
  ButtonToolbar,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  CardTitle,
  CardText,
  Col,
  Dropdown,
  DropdownItem,
  DropdownMenu,
  DropdownToggle,
  Progress,
  Row,
  Table,
} from 'reactstrap';
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips';
import { getStyle, hexToRgba } from '@coreui/coreui/dist/js/coreui-utilities'

class Campaign extends Component {
  constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);

    this.state = {
      dropdownOpen: false,
      radioSelected: 2,
      items: []
    };
  }

  toggle() {
    this.setState({
      dropdownOpen: !this.state.dropdownOpen,
    });
  }

  onRadioBtnClick(radioSelected) {
    this.setState({
      radioSelected: radioSelected,
    });
  }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  componentDidMount() {
    fetch('http://127.0.0.1:5000/getDetail')

   .then((resp) => {

      resp.json()
      console.log(resp)
    })
   .then(resp => {
     const campaigns = resp;
     console.log("Response", campaigns);
     this.setState({
       items: campaigns
     });
   })
  }

  render() {
    const {items} = this.state;
    return (
      <div className="animated fadeIn">
       <div>
       {/* <ul>
          {items.map(item => (
            <li key={item.CampaignName}>
              {item.CampaignName}
            </li>
          ))}
        </ul>
       <h3>Welcome</h3> */}
       {/* <h4> {this.state.items.CampaignName} </h4> */}
      <Card>
        {/* <CardHeader>{this.state.items.CampaignName}</CardHeader> */}
        <CardBody>
          <CardText>Email</CardText>
        </CardBody>
        <CardFooter>Completed</CardFooter>
      </Card>
      </div>

      </div>
    );
  }
}

export default Campaign;
