import React, { Component, lazy, Suspense } from 'react';
import { BrowserRouter as Router,Route, Link} from 'react-router-dom';
import NewCampaign from '../NewCampaign/NewCampaign';
import {
  Badge,
  Button,
  ButtonDropdown,
  ButtonGroup,
  ButtonToolbar,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  CardTitle,
  CardText,
  Col,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Dropdown,
  Progress,
  Row,
  Table,
  FormGroup, Label, Input
} from 'reactstrap';
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips';
import { getStyle, hexToRgba } from '@coreui/coreui/dist/js/coreui-utilities'

class Campaign extends Component {
  constructor(props) {
    super(props);

    this.state = {
      CampaignList: true,
      CreateCampaign: false,
      items: []
    };

    this.handleButtonClick = this.handleButtonClick.bind(this);
  }


  handleButtonClick()
  {
    this.setState({
       showComponent: true,
    });
  }


  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>

  componentDidMount() {
    fetch('http://127.0.0.1:5000/getDetail')
    .then(resp => resp.json())
    .then((resp) => {
     this.setState({

       items: resp.CList
     });
   })
  }

  render() {

    const {items} = this.state;
    if(CampaignList){
    return (

      <div className="animated fadeIn">
        <div>
          <Row>
            <Col sm="6"><h2>Campaigns</h2></Col>
            <Col sm="4"></Col>
            <Col sm="2"> 
            {/* <Router>
            <Link exact to='/campaigns/new-campaign' className="links"> Campaign </Link>
            <Route exact path="/campaigns/new-campaign" component={NewCampaign} />
            </Router> */}
            <Button style={{color:"white", backgroundColor: "black"}} size="sm" onClick={this.handleButtonClick} > &#43;&#160; &#160;Campaign</Button>
            {this.state.showComponent ?
           <NewCampaign /> :
           null
        }
            </Col>
          </Row>
        </div> <br/>
          <div> 
            <Row> 
              <Col sm="4"> <Label for="exampleSearch">Your Campaigns</Label></Col>
              <Col sm="1">
                  <Label for="exampleSearch">Search</Label>   
              </Col>
              <Col sm="3">
                  <Input
                    type="search"
                    name="search"
                    id="exampleSearch"
                    placeholder="search placeholder"
                    size="sm"
                    onClick = {this.handleSearch}
                  /> 
              </Col>
              <Col sm="4"> </Col>
            </Row>
          </div>
          <br/>


        <Row>

          {items.map((item, index) => (     
            <Col xs="12" sm="6" lg="3">
              <Card >
                <CardHeader style={{fontSize:15}}>{item.CampaignName} 
                  <ButtonGroup className="float-right">
                  <ButtonDropdown id={item.CampaignId} isOpen={this.state[item.CampaignId]} toggle={() => { this.setState({ [item.CampaignId]: !this.state[item.CampaignId]}); }}>
                  <DropdownToggle >
                  <i class="fa fa-ellipsis-v"></i>
                  </DropdownToggle>
                  <DropdownMenu right>
                  <DropdownItem>Edit</DropdownItem>
                  <DropdownItem>Duplicate</DropdownItem>
                  <DropdownItem>Archive</DropdownItem>
                  </DropdownMenu> 
                  </ButtonDropdown>
                  </ButtonGroup>
                </CardHeader>
                <CardBody>
                  <CardText>{item.Channel}</CardText>
                </CardBody>
                <CardFooter className ={(item.Status)==="Completed"? 'completed':(item.Status)==="Running"?'running': (item.Status)==="Scheduled"?'scheduled':(item.Status)==="Archived"?'archived':'draft' }> {item.Status} </CardFooter>
              </Card>
            </Col>

          ))}
        </Row>     
      </div> 

    );
   }
  }
}
export default Campaign;
