import React, { Component, lazy, Suspense } from 'react';
import { Bar, Line } from 'react-chartjs-2';
import {
  Badge,
  Button,
  ButtonDropdown,
  ButtonGroup,
  ButtonToolbar,
  Card,
  CardBody,
  CardFooter,
  CardHeader,
  CardTitle,
  CardText,
  Col,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  Dropdown,
  Progress,
  Row,
  Table,
  FormGroup, Label, Input
} from 'reactstrap';
import { CustomTooltips } from '@coreui/coreui-plugin-chartjs-custom-tooltips';
import { getStyle, hexToRgba } from '@coreui/coreui/dist/js/coreui-utilities'

class Campaign extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {
      dropdownOpen: false,
      items: []
    };
  }

  // toggle() {
  //   this.setState(prevState => ({
  //     dropdownOpen: !prevState.dropdownOpen
  //   }));
  // }
  // toggle() {
  //   this.setState({
  //    isOpen: !this.state.isOpen
  //   });
  // }

  loading = () => <div className="animated fadeIn pt-1 text-center">Loading...</div>
  handleClick(e, id){
       console.log("Button Click",id)  
  }


  componentDidMount() {
    fetch('http://127.0.0.1:5000/getDetail')
    .then(resp => resp.json())
    .then((resp) => {
     this.setState({

       items: resp.CList
     });
   })
  }



  render() {
    const {items} = this.state;
    return (
      <div className="animated fadeIn">
        <div>
          <Row>
            <Col sm="6"><h2>Campaigns</h2></Col>
            <Col sm="4"></Col>
            <Col sm="2"> <Button style={{color:"white", backgroundColor: "black"}} size="sm"> &#43;&#160; &#160;Campaign</Button></Col>
          </Row>
        </div> <br/>
          <div> 
            <Row> 
              <Col sm="4"> <Label for="exampleSearch">Your Campaigns</Label></Col>
              <Col sm="1">
                  <Label for="exampleSearch">Search</Label>   
              </Col>
              <Col sm="3">
                  <Input
                    type="search"
                    name="search"
                    id="exampleSearch"
                    placeholder="search placeholder"
                    size="sm"
                    onClick = {this.handleSearch}
                  /> 
              </Col>
              <Col sm="4"> </Col>
            </Row>
          </div>
          <br/>

        {/* <div  className ="CampaignPortofolio" style={{display:"Grid", gridTemplateColumns:"190px 190px 190px 190px 190px", gridGap:20}}> */}
        <Row>
            {/* <Col xs="12" sm="6" lg="3">
              <Card className="text-white bg-info">
                <CardBody className="pb-0">
                  <ButtonGroup className="float-right">
                  <ButtonDropdown id='card1' isOpen={this.state.card1} toggle={() => { this.setState({ card1: !this.state.card1 }); }}>
                  <DropdownToggle caret className="p-0" color="transparent">
                  <i className="icon-settings"></i>
                  </DropdownToggle>
                  <DropdownMenu right>
                  <DropdownItem>Action</DropdownItem>
                  <DropdownItem>Another action</DropdownItem>
                  <DropdownItem disabled>Disabled action</DropdownItem>
                  <DropdownItem>Something else here</DropdownItem>
                  </DropdownMenu>
                  </ButtonDropdown>
                  </ButtonGroup>
                  <div className="text-value">9.823</div>
                  <div>Members online</div>
                </CardBody>
              </Card>
            </Col> */}

          {items.map((item, index) => (     
            <Col xs="12" sm="6" lg="3">
              <Card >
                <CardHeader style={{fontSize:20}}>{item.CampaignName} 
                  <ButtonGroup className="float-right">
                  <ButtonDropdown id={item.CampaignId}  onClick={(e, id)=>this.handleClick}>
                  <DropdownToggle >
                  <i class="fa fa-ellipsis-v"></i>
                  </DropdownToggle>
                  <DropdownMenu right >
                  <DropdownItem>Edit</DropdownItem>
                  <DropdownItem>Duplicate</DropdownItem>
                  <DropdownItem>Archive</DropdownItem>
                  </DropdownMenu>
                  </ButtonDropdown>
                  </ButtonGroup>
                </CardHeader>
                <CardBody>
                  <CardText>{item.Channel}</CardText>
                </CardBody>
                <CardFooter className ={(item.Status)==="Completed"? 'completed':(item.Status)==="Running"?'running': (item.Status)==="Scheduled"?'scheduled':(item.Status)==="Archived"?'archived':'draft' }> {item.Status} </CardFooter>
              </Card>
            </Col>

          ))}
        </Row>
        {/* </div> */}

      </div>      
    );
  }
}
export default Campaign;
